import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { watchAPI } from '../services/api';
import Swal from 'sweetalert2';
import './Home.css';

function Home() {
  const [watches, setWatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchWatches();
  }, []);

  const fetchWatches = async () => {
    try {
      setLoading(true);
      const response = await watchAPI.getAllWatches();
      setWatches(response.data);
    } catch (error) {
      Swal.fire('Error', 'Failed to load watches', 'error');
    } finally {
      setLoading(false);
    }
  };

  const addToCart = (watch) => {
    const cart = JSON.parse(localStorage.getItem('cart')) || [];
    const existingItem = cart.find((item) => item._id === watch._id);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      cart.push({ ...watch, quantity: 1 });
    }

    localStorage.setItem('cart', JSON.stringify(cart));
    localStorage.setItem('cartCount', cart.reduce((sum, item) => sum + item.quantity, 0));

    Swal.fire('Success', 'Watch added to cart', 'success');
    window.dispatchEvent(new Event('cartUpdated'));
  };

  return (
    <Container className="my-5">
      <div className="text-center mb-5">
        <h1>Welcome to WatchMate</h1>
        <p className="lead">Elegance that endures</p>
      </div>

      {loading ? (
        <div className="text-center">
          <Spinner animation="border" />
        </div>
      ) : (
        <Row>
          {watches.map((watch) => (
            <Col md={6} lg={4} key={watch._id} className="mb-4">
              <Card className="watch-card h-100">
                <Card.Img
                  variant="top"
                  src={`/images/watches/${watch.image}`}
                  alt={watch.title}
                  height="250"
                  style={{ objectFit: 'cover' }}
                />
                <Card.Body>
                  <Card.Title>{watch.title}</Card.Title>
                  <Card.Text className="text-muted">{watch.description}</Card.Text>
                  <div className="price mb-3">â‚¹{watch.price}</div>
                  <div className="d-grid gap-2">
                    <Link to={`/watch/${watch._id}`} className="btn btn-outline-primary">
                      View Details
                    </Link>
                    <Button variant="primary" onClick={() => addToCart(watch)}>
                      Add to Cart
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}

export default Home;
